import { toast } from "react-hot-toast";
import { useDispatch, useSelector } from "react-redux";
import { add, remove } from "../redux/Slices/CartSlice";

const Product = ({post}) => {

  const {cart} = useSelector( (state) => state);
  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch(add(post));
    toast.success("Item added to Cart");
  }

  const removeFromCart = () => {
    dispatch(remove(post.id));
    toast.error("Item removed from cart");
  }


  return (
    <div className="flex flex-col items-center justify-center justify-between hover:scale-110 transition duration-300 ease-in gap-3 p-4 mt-10 ml-5 rounded-xl outline">
      <div>
        <p className="text-gray-700 font-semibold text-left truncate text-lg w-40 mt-1">{post.title}</p>
      </div>

      <div>
        <p className="w-40 text-gray-400 font-normal text-[10px] text-left">{post.description.split(" ").slice(0,10).join(" ") + "..."}</p>
      </div>

      <div>
        <img alt="" src={post.image} className="h-full w-full"/>
      </div> 

      <div className="flex justify-between gap-12">
        <div>
          <p className="text-green-600 font-semibold">${post.price}</p>
        </div>

        {
          cart.some( (p) => p.id === post.id) ? 
          (<button onClick={removeFromCart}>
            Remove Item
          </button>) : 
          (<button onClick={addToCart}>
            Add to Card
          </button>)
        }
      </div>

    </div>
  );
};

export default Product;
